import React from 'react'
import Navbar from "react-bootstrap/Navbar"
import Nav from "react-bootstrap/Nav"
import Logo from "../../assets/logo_ee.png";
import UserIcon from "../../assets/user_icon_white.svg";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom";
import "./navbar.css"
import { resetUser } from '../../redux/actions/userActions';
import { resetPositions } from '../../redux/actions/listActions';
import { resetTitle } from '../../redux/actions/listTitleActions';
import { NavDropdown } from "react-bootstrap";
import CRUDService from '../../service/CRUDService';


export class NavbarComponent extends React.Component {
    constructor(props) {
        super(props);
            this.handleUserSettingsClick = this.handleUserSettingsClick.bind(this);
            this.handleUserLoginClick = this.handleUserLoginClick.bind(this);
            this.handleUserLogoutClick = this.handleUserLogoutClick.bind(this);
    }




    handleUserSettingsClick() {
        this.props.history.push("/settings-user");
    }

    handleUserLoginClick() {
        this.props.history.push("/user");
    }

    handleUserLogoutClick() {
        this.props.history.push("/");
        this.props.resetUser();
        CRUDService.resetLocalStorage();
    }

    resetFunction = () =>{
        this.props.resetPositions()
        this.props.resetTitle()
    }

    render() {

        const {username} = this.props.currentUser;

        return (
            <div>
                <Navbar className="nav-wrapper" sticky="top" collapseOnSelect expand="lg">
                    <Navbar.Brand>
                        <img className="nav-logo" src={Logo} alt="logo"></img>
                    </Navbar.Brand>
                    <Nav>
                        <Link to="/" className="nav-link nav-link-home">
                            HOME 
                            <hr />
                        </Link>
                        <Link to="/shopping-cart" onClick={()=>{this.resetFunction()}}className="nav-link nav-link-new-shoppingcart">
                            NEW SHOPPINGCART
                            <hr />
                        </Link>
                        <Link to="/saved-shoppingcarts" className="nav-link nav-link-my-shoppingcarts">
                            MY SHOPPINGCARTS                            
                            <hr />
                        </Link>
                    </Nav>
                    <Navbar.Collapse className="justify-content-end"> 
                        <Nav >
                            {username === '' ? 
                                <Link to='/user' className="nav-link nav-link-login">
                                <img className="user-icon" src={UserIcon} alt="user-icon"></img>
                                Login                                     
                                <hr />
                                </Link>
                            : 
                                <div className="nav-link nav-link-login">
                                <img className="user-icon" src={UserIcon} alt="user-icon"></img>                              
                                        <NavDropdown className="nav-dropdown" title={username} id="basic-nav-dropdown">
                                        <NavDropdown.Item onClick={this.handleUserSettingsClick}>User settings</NavDropdown.Item>
                                        <NavDropdown.Item onClick={this.handleUserLogoutClick}>Logout</NavDropdown.Item>                                     </NavDropdown> 
                                <hr />
                                </div>                            
                            }                   
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
            </div>
        )
    }
}


const mapStateToProps = state => ({        
    currentUser: state.currentUser
})

const mapDispatchToProps = dispatch => ({               //actions
    resetUser: () => dispatch(resetUser()),    //open dispacth with action changeTitle
    resetPositions:() => dispatch(resetPositions()),
    resetTitle : () => dispatch(resetTitle())

})


export default withRouter(connect(mapStateToProps,mapDispatchToProps)(NavbarComponent))
